import React, { useState, useEffect } from 'react';
import axios from 'axios';

function ClienteForm() {
  const [nombre, setNombre] = useState('');
  const [numeroDocumento, setNumeroDocumento] = useState('');
  const [direccion, setDireccion] = useState('');
  const [telefono, setTelefono] = useState('');
  const [email, setEmail] = useState('');
  const [tipoDocumentoId, setTipoDocumentoId] = useState('');
  const [tiposDocumento, setTiposDocumento] = useState([]);

  // Cargar tipos de documento desde el backend
  useEffect(() => {
    axios.get('http://localhost:8080/api/tipodocumento') // Ajusta esta URL si es diferente
      .then(response => {
        setTiposDocumento(response.data);
      })
      .catch(error => {
        console.error('Error al cargar tipos de documento', error);
      });
  }, []);

  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const cliente = {
        nombre: nombre,
        numeroDocumento: numeroDocumento,
        direccion: direccion,
        telefono: telefono,
        email: email,
        idTipoDocumento: {
          id: parseInt(tipoDocumentoId)
        }
      };

      const response = await axios.post('http://localhost:8080/api/clientes', cliente);
      alert('Cliente registrado exitosamente');
      console.log('Respuesta:', response.data);
    } catch (error) {
      console.error('Error al registrar cliente:', error);
      alert('Ocurrió un error. Verifica los datos e inténtalo de nuevo.');
    }
  };

  return (
    <div>
      <h2>Registrar Cliente</h2>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Nombre"
          value={nombre}
          onChange={(e) => setNombre(e.target.value)}
          required
        /><br />

        <input
          type="text"
          placeholder="Número Documento"
          value={numeroDocumento}
          onChange={(e) => setNumeroDocumento(e.target.value)}
          required
        /><br />

        <input
          type="text"
          placeholder="Dirección"
          value={direccion}
          onChange={(e) => setDireccion(e.target.value)}
          required
        /><br />

        <input
          type="text"
          placeholder="Teléfono"
          value={telefono}
          onChange={(e) => setTelefono(e.target.value)}
          required
        /><br />

        <input
          type="email"
          placeholder="Correo electrónico"
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          required
        /><br />

        <select
          value={tipoDocumentoId}
          onChange={(e) => setTipoDocumentoId(e.target.value)}
          required
        >
          <option value="">Selecciona tipo de documento</option>
          {tiposDocumento.map((tipo) => (
            <option key={tipo.id} value={tipo.id}>
              {tipo.nombre}
            </option>
          ))}
        </select><br /><br />

        <button type="submit">Guardar Cliente</button>
      </form>
    </div>
  );
}

export default ClienteForm;
